package edu.amrita.selabs.cumulus.lib.test;

import static org.junit.Assert.*;


import java.security.Key;

import org.junit.Test;

import edu.amrita.selabs.cumulus.lib.RSACryptoUtil;
import edu.amrita.selabs.cumulus.lib.RSAKeyUtil;

public class RSACryptoUtilTest {

	String sPubKey = "";
	
	String sPrivKey = "";

	@Test
	public void test() throws Exception{
		
			String data = "Aum Amriteswaryai Namah!";

			RSAKeyUtil keyUtil = new RSAKeyUtil();
			Key privKey = keyUtil.decodePrivateKey(sPrivKey);
			
			RSACryptoUtil cUtil = new RSACryptoUtil();
			
			String sign = cUtil.sign(data,  privKey);
			
			Key pubKey = keyUtil.decodePublicKey(sPubKey);			
			
			assertTrue(cUtil.verify(data,  sign,  pubKey));			

	}

}
